// src/App.js

import React, { useState, Suspense } from 'react';
import { AppProvider } from './context/AppContext';
import { EventProvider } from './context/EventContext';

// Lazy load components
const Hero = React.lazy(() => import('./components/Hero'));
const Activities = React.lazy(() => import('./components/Activities'));
const Community = React.lazy(() => import('./components/Community'));
const CommunityJoin = React.lazy(() => import('./components/community/CommunityJoin'));
const FounderSection = React.lazy(() => import('./components/FounderSection'));
const Events = React.lazy(() => import('./components/events'));
const Navigation = React.lazy(() => import('./components/Navigation/Navigation'));

// Simple loading component with brand colors
const PageLoader = () => (
  <div className="min-h-screen bg-gray-900 flex items-center justify-center">
    <div className="text-center">
      <i className="fas fa-dragon text-orange-500 text-4xl mb-4 animate-bounce" />
      <p className="text-gray-300">Loading Dancing Dragons...</p>
    </div>
  </div>
);

function AppContent() {
  const [currentPath, setCurrentPath] = useState('/');

  const handleNavigate = (path) => {
    setCurrentPath(path);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Simplified content rendering with error boundaries
  const renderContent = () => {
    const routes = {
      '/': (
        <>
          <Hero />
          <Activities />
          <FounderSection />
        </>
      ),
      '/activities': <Activities fullPage={true} />,
      '/community': <Community />,
      '/events': <Events />,
      '/join': <CommunityJoin />
    };

    return routes[currentPath] || routes['/'];
  };

  return (
    <div className="bg-gray-900 min-h-screen">
      <Suspense fallback={<PageLoader />}>
        <Navigation 
          currentPath={currentPath}
          onNavigate={handleNavigate}
        />
        {renderContent()}
      </Suspense>
    </div>
  );
}

function App() {
  return (
    <AppProvider>
      <EventProvider>
        <AppContent />
      </EventProvider>
    </AppProvider>
  );
}

export default App;
