import React, { useState, useEffect } from 'react';
import { SignalContactModal } from '../system/SignalContactModal';

function Navigation({ currentPath, onNavigate }) {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [isSignalModalOpen, setIsSignalModalOpen] = useState(false);

  // Handle scroll effects
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Lock body scroll when menu is open
  useEffect(() => {
    if (isMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }
    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [isMenuOpen]);

  const handleNavClick = (path) => {
    onNavigate(path);
    setIsMenuOpen(false);
  };

  return (
    <>
      <header 
        className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 
                   ${isScrolled ? 'bg-gray-900/95 backdrop-blur-md shadow-xl' : 'bg-transparent'}`}
      >
        <div className="max-w-7xl mx-auto px-4">
          <div className="flex items-center justify-between h-16">
            {/* Logo */}
            <button 
              onClick={() => handleNavClick('/')}
              className="flex items-center gap-2 text-white hover:text-orange-400 transition-colors"
            >
              <i className="fas fa-dragon text-orange-500 text-2xl" />
              <span className="font-bold text-lg">Dancing Dragons</span>
            </button>

            {/* Mobile Menu Button */}
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="p-2 text-gray-300 hover:text-white transition-colors"
              aria-label="Toggle menu"
            >
              <i className={`fas ${isMenuOpen ? 'fa-times' : 'fa-bars'} text-2xl`} />
            </button>
          </div>
        </div>

        {/* Mobile Menu Overlay */}
        <div 
          className={`fixed inset-0 bg-gray-900/95 backdrop-blur-md transition-transform duration-300 transform 
                     ${isMenuOpen ? 'translate-y-0' : '-translate-y-full'}`}
          style={{ top: '64px' }}
        >
          <div className="max-w-7xl mx-auto px-4 py-8 h-[calc(100vh-64px)] flex flex-col">
            {/* Navigation Links */}
            <nav className="space-y-2 mb-8">
              {[
                { path: '/', label: 'Home', icon: 'fa-home' },
                { path: '/activities', label: 'Activities', icon: 'fa-mountain' },
                { path: '/community', label: 'Community', icon: 'fa-users' },
                { path: '/events', label: 'Events', icon: 'fa-calendar' }
              ].map(({ path, label, icon }) => (
                <button
                  key={path}
                  onClick={() => handleNavClick(path)}
                  className={`w-full flex items-center gap-4 px-4 py-3 rounded-lg transition-colors
                             ${currentPath === path ? 
                               'bg-orange-500 text-white' : 
                               'text-gray-300 hover:bg-gray-800'}`}
                >
                  <i className={`fas ${icon} w-6`} />
                  <span className="text-lg">{label}</span>
                </button>
              ))}
            </nav>

            {/* Join Button */}
            <button
              onClick={() => {
                setIsSignalModalOpen(true);
                setIsMenuOpen(false);
              }}
              className="mt-auto mb-8 bg-orange-500 hover:bg-orange-600 text-white 
                       py-4 px-6 rounded-xl text-lg font-semibold w-full
                       transition-all duration-300 flex items-center justify-center gap-2"
            >
              <i className="fas fa-user-plus" />
              Join Our Community
            </button>

            {/* Privacy Notice */}
            <div className="text-center text-sm text-gray-400">
              <i className="fas fa-shield-alt mr-2" />
              End-to-end encrypted â€¢ Zero data storage
            </div>
          </div>
        </div>
      </header>

      {/* Spacer for fixed header */}
      <div className="h-16" />

      <SignalContactModal 
        isOpen={isSignalModalOpen}
        onClose={() => setIsSignalModalOpen(false)}
      />
    </>
  );
}

export default Navigation;
